const handleSubmit = async (e) => {
    e.preventDefault();

    console.log("Submitting event form...");  // Debugging log

    const eventData = {
        name,
        description,
        date,
        location,
    };

    console.log("Event Data:", eventData);  // Check if data is correct before sending

    try {
        const response = await axios.post(
            "https://event-management-backend-o93a.onrender.com/api/events",
            eventData,
            { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
        );

        console.log("Response:", response.data); // Check what the API returns

        if (response.status === 201) {
            alert("Event Created Successfully!");
            window.location.href = "/dashboard";
        }
    } catch (error) {
        console.error("Error creating event:", error.response ? error.response.data : error);
        alert("Failed to create event.");
    }
};
