import React, { useState, useEffect } from "react";
import axios from "axios";
import { io } from "socket.io-client";

const socket = io("https://event-management-backend-o93a.onrender.com"); // Replace with your backend URL

const Dashboard = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const token = localStorage.getItem("token");
                if (!token) return alert("You must be logged in!");

                const res = await axios.get("https://event-management-backend-o93a.onrender.com/api/events", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                // Add attendee count to each event
                const eventsWithAttendees = res.data.map(event => ({
                    ...event,
                    attendees: 0, // Default count
                }));
                setEvents(eventsWithAttendees);
            } catch (err) {
                alert("Error fetching events");
            }
        };

        fetchEvents();
    }, []);

    useEffect(() => {
        socket.on("updateAttendees", ({ eventId, count }) => {
            setEvents((prevEvents) =>
                prevEvents.map((event) =>
                    event._id === eventId ? { ...event, attendees: count } : event
                )
            );
        });

        return () => {
            socket.off("updateAttendees");
        };
    }, []);

    const handleJoinEvent = (eventId) => {
        socket.emit("joinEvent", eventId);
    };

    return (
        <div className="container mt-5">
            <h2>Your Events</h2>
            <ul className="list-group">
                {events.map((event) => (
                    <li key={event._id} className="list-group-item">
                        <strong>{event.name}</strong> - {event.location} <br />
                        <strong>Attendees:</strong> {event.attendees || 0} <br />
                        <button className="btn btn-primary mt-2" onClick={() => handleJoinEvent(event._id)}>
                            Join Event
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Dashboard;
